package leap.year;
import java.util.*;
public class leapyear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int year=2000;
if(year%400==0)
	System.out.println("leap year");
else if(year%100==0)
	System.out.println("not leap year");
else if(year%4==0)
	System.out.println("leap year");
	}

}
